		if(!empty($layout) && ($layoutFile=$this->getLayoutFile($layout))!==false)
			$output=$this->renderFile($layoutFile,array('content'=>$output),true);
		$output=$this->processOutput($output);
		if($return)
			return $output;
		else
			echo $output;
	}
	public function renderText($text,$return=false)
	{
