class CLinkPager extends CBasePager
{
	public $maxButtonCount=10;
	public $nextPageLabel='Next >>';
	public $prevPageLabel='<< Prev';
	public $firstPageLabel='|<< First';
	public $lastPageLabel='Last >>|';
	public $showFirstPageButton=false;
	public $showLastPageButton=false;
	public $buttonSeparator="\n";
	public $header='Go to page: ';
	public $footer='';
	public $htmlOptions=array();
	public function run()
	{
		if(($pageCount=$this->getPageCount())<=1)
			return;
		list($beginPage,$endPage)=$this->getPageRange();
		$currentPage=$this->getCurrentPage();
		$controller=$this->getController();
		$params=$_GET;
		$buttons=array();
		if($beginPage>0 && $this->showFirstPageButton)
			$buttons[]=$this->createPageButton($this->firstPageLabel,0,$currentPage);
		if($currentPage>0)
			$buttons[]=$this->createPageButton($this->prevPageLabel,$currentPage-1,$currentPage);
		for($i=$beginPage;$i<=$endPage;++$i)
			$buttons[]=$this->createPageButton($i+1,$i,$currentPage);
		if($currentPage<$pageCount-1)
			$buttons[]=$this->createPageButton($this->nextPageLabel,$currentPage+1,$currentPage);
		if($endPage<$pageCount-1 && $this->showLastPageButton)
			$buttons[]=$this->createPageButton($this->lastPageLabel,$pageCount-1,$currentPage);
		$content=implode($this->buttonSeparator,$buttons);
		$htmlOptions=$this->htmlOptions;
		if(!isset($htmlOptions['id']))
			$htmlOptions['id']=$this->getId();
		echo CHtml::tag('div',$htmlOptions,$this->header.$content.$this->footer);
	}
	protected function getPageRange()
	{
		$currentPage=$this->getCurrentPage();
		$pageCount=$this->getPageCount();
		$buttonCount=$this->maxButtonCount > $pageCount ? $pageCount : $this->maxButtonCount;
		$beginPage=0;
		$endPage=$buttonCount-1;
		if($currentPage>$endPage)
		{
			$beginPage=((int)($currentPage/$this->maxButtonCount))*$this->maxButtonCount;
			if(($endPage=$beginPage+$this->maxButtonCount-1)>=$pageCount)
				$endPage=$pageCount-1;
		}
		return array($beginPage,$endPage);
	}
	protected function createPageButton($label,$page,$currentPage)
	{
		if($page===$currentPage)
			return '<span>'.$label.'</span>';
		else
			return '<span>'.CHtml::link($label,$this->createPageUrl($page)).'</span>';
	}
}
class CAssetManager extends CApplicationComponent
{
	const DEFAULT_BASEPATH='assets';
	private $_basePath;
	private $_baseUrl;
	private $_published=array();
	public function init()
	{
		parent::init();
		$request=Yii::app()->getRequest();
		if($this->getBasePath()===null)
			$this->setBasePath(dirname($request->getScriptFile()).DIRECTORY_SEPARATOR.self::DEFAULT_BASEPATH);
		if($this->getBaseUrl()===null)
			$this->setBaseUrl($request->getBaseUrl().'/'.self::DEFAULT_BASEPATH);
	}
	public function getBasePath()
	{
		return $this->_basePath;
	}
	public function setBasePath($value)
	{
		if(($basePath=realpath($value))!==false && is_dir($basePath) && is_writable($basePath))
			$this->_basePath=$basePath;
		else
			throw new CException(Yii::t('yii','CAssetManager.basePath "{path}" is invalid. Please make sure the directory exists and is writable by the Web server process.',
				array('{path}'=>$value)));
	}
	public function getBaseUrl()
	{
		return $this->_baseUrl;
	}
	public function setBaseUrl($value)
	{
		$this->_baseUrl=rtrim($value,'/');
	}
	public function publish($path,$level=-1)
	{
		if(isset($this->_published[$path]))
			return $this->_published[$path];
		else if(($src=realpath($path))!==false)
		{
			if(is_file($src))
			{
				$dir=$this->hash(dirname($src));
				$fileName=basename($src);
				$dstDir=$this->getBasePath().DIRECTORY_SEPARATOR.$dir;
				$dstFile=$dstDir.DIRECTORY_SEPARATOR.$fileName;
				if(@filemtime($dstFile)<@filemtime($src))
				{
					if(!is_dir($dstDir))
					{
						mkdir($dstDir);
						@chmod($dstDir,0777);
					}
					copy($src,$dstFile);
				}
				return $this->_published[$path]=$this->getBaseUrl()."/$dir/$fileName";
			}
			else
			{
				$dir=$this->hash($src);
				$dstDir=$this->getBasePath().DIRECTORY_SEPARATOR.$dir;
				if(!is_dir($dstDir))
					CFileHelper::copyDirectory($src,$dstDir,array('exclude'=>array('.svn'),'level'=>$level));
				return $this->_published[$path]=$this->getBaseUrl().'/'.$dir;
			}
		}
		else
			throw new CException(Yii::t('yii','The asset "{asset}" to be pulished does not exist.',
				array('{asset}'=>$path)));
	}
	public function getPublishedPath($path)
	{
		if(($path=realpath($path))!==false)
		{
			if(is_file($path))
				return $this->getBasePath().DIRECTORY_SEPARATOR.$this->hash(dirname($path)).DIRECTORY_SEPARATOR.basename($path);
			else
				return $this->getBasePath().DIRECTORY_SEPARATOR.$this->hash($path);
		}
		else
			return false;
	}
	public function getPublishedUrl($path)
	{
		if(isset($this->_published[$path]))
			return $this->_published[$path];
		if(($path=realpath($path))!==false)
		{
			if(is_file($path))
				return $this->getBaseUrl().'/'.$this->hash(dirname($path)).'/'.basename($path);
			else
				return $this->getBaseUrl().'/'.$this->hash($path);
		}
		else
			return false;
	}
	protected function hash($path)
	{
		return sprintf('%x',crc32($path.Yii::getVersion()));
	}
}
